        snakeX[1]=75;snakeY[1]=100; //ç¬¬ä¸€èŠ‚èº«ä½“çš„åæ ‡
        snakeX[2]=50;snakeY[2]=100; //ç¬¬äºŒèŠ‚èº«ä½“çš„åæ ‡
        fx="R";

        //äº‹åŠ¡ä½ç½®éšæœºåˆ†å¸ƒ
        foodX=25+25*random.nextInt(24);
