        }

        //è½¬æ¢ä¸ºç¨€ç–æ•°ç»„
        //è·å–æœ‰æ•ˆå€¼çš„ä¸ªæ•°
        int sum=0;
        for(int i=0;i<11;i++){
            for(int j=0;j<11;j++){
                if(array1[i][j]!=0) sum++;
            }
        }

        //åˆ›å»ºä¸€ä¸ªç¨€ç–æ•°ç»„çš„æ•°ç»„
        /*
        * array[0][0]å­˜å‚¨è¡Œæ•°ï¼Œarray[0][1]å­˜å‚¨åˆ—æ•°ï¼Œarray[0][2]å­˜å‚¨é0å…ƒç´ çš„ä¸ªæ•°ï¼Œ
        * å¦å¤–ï¼Œå¯¹äºarray2é™¤äº†ç¬¬0è¡Œçš„æ¯ä¸€è¡Œarray2[i]
        * array[i][0]å­˜å‚¨é0å…ƒç´ æ‰€åœ¨çš„è¡Œ
        * array[i][1]å­˜å‚¨é0å…ƒç´ æ‰€åœ¨çš„åˆ—
        * array[i][2]å­˜å‚¨é0å…ƒç´ çš„å€¼
        */
        int[][] array2=new int[sum+1][3];
        array2[0][0]=array2[0][1]=11;
        array2[0][2]=sum;

        int count=0;

        for(int i=0;i<array1.length;i++){
            for(int j=0;j<array1.length;j++){
                if(array1[i][j]!=0){
                    count++;
                    array2[count][0]=i;
                    array2[count][1]=j;
                    array2[count][2]=array1[i][j];
                }
            }
        }

        //è¾“å‡ºç¨€ç–æ•°ç»„
        System.out.println("è¾“å‡ºç¨€ç–æ•°ç»„");
        for(int i=0;i<array2.length;i++){
