        }
        //=========================//
        System.out.println("================================");
        System.out.println("è¿˜åŸ");
        //1.è¯»å–ç¨€ç–æ•°ç»„
        int[][] array3=new int[array2[0][0]][array2[0][1]];

        //2.è¿˜åŸå€¼
        for(int i=1;i<array2.length;i++){
            array3[array2[i][0]][array2[i][1]]=array2[i][2];
        }

        System.out.println("è¾“å‡ºè¿˜åŸçš„æ•°ç»„");
        for(int i=0;i<11;i++){
